import React from 'react'
import { useEffect, useState } from "react";
import httpClients from '../httpClients';
import EventItem from './EventItem';


export default function EventsList(props) {
    const [listEvents, setListEvents] = useState([])
    const [currentPage, setCurrentPage] = useState(0)
    const [deletedElement, setDeletedElement] = useState(false)

    useEffect(() => {
        httpClients.get('//localhost:5000/events/'+currentPage,
        {
           'offset': 0
        }).then((events) => {
            setListEvents(events.data)
        })
    }, [currentPage, deletedElement])

    const renderedList = listEvents.map((event) => {
        return <EventItem event={event} deletedElement={deletedElement} setDeletedElement={setDeletedElement} setSelectedEvent={props.setSelectedEvent}/>
    })

    return (
        <div className="ui relaxed divided list">
            {renderedList}
        </div>
    )
}
