import React, { useEffect, useState } from 'react';
import EventsList from './EventsList';
import EventDetail from './EventDetail';

const defaultValuesEvent = {event:{nombre:'',lugar:'',direccion:'',fecha_inicio:'',fecha_fin:'', presencial:false}}

export default function EventsHandler(props) {
    const [selectedEvent, setSelectedEvent] = useState(defaultValuesEvent)

    return (
        <div className='ui grid'>
            <div className='ui row'>
                <div className= 'seven wide column'>
                    <button onClick class="positive ui button">Crear Evento</button>
                    <EventsList setSelectedEvent ={setSelectedEvent}/>
                </div>
                <div className = 'seven wide column'>
                    <EventDetail selectedEvent={selectedEvent}/>
                </div>

            </div>
        </div>
    )
}
