import {BrowserRouter, Route} from "react-router-dom";
import { useEffect, useState } from "react";
import LoginPage from "./components/LoginPage";
import RegisterPage from "./components/RegisterPage";
import httpClients from "./httpClients";
import EventsHandler from "./components/EventsHandler";

function App() {
  const [loggedIn, setLoggedIn] = useState(false)
  const [userInfo, setUserInfo] = useState(null)


  useEffect(()=>{
    httpClients.get('//localhost:5000/@me').then((response)=>{
      setLoggedIn(true);
      setUserInfo(response.data)
    }).catch(()=>{
      setLoggedIn(false);
      setUserInfo(null);
    })
  }, [loggedIn]);



  return (
    <div className='ui container'>
      {loggedIn==false?(
        <div className='conteiner_center'>
        <BrowserRouter>
          <Route path="/" exact render={() => <LoginPage setLoggedIn={setLoggedIn}/>}/>
          <Route path='/register' exact component={RegisterPage}/>
          {/* <Route exact component={NotFound}/> */}
        </BrowserRouter>
        </div>
      ):
        <BrowserRouter>
          <Route path="/" exact render={() => <EventsHandler user={userInfo}/>}/>
        </BrowserRouter>
    }
    </div>
  );
}

export default App;
