function App() {
  const [loggedIn, setLoggedIn] = useState(false)
  const [userInfo, setUserInfo] = useState(null)


  useEffect(()=>{
    httpClients.get('//localhost:5000/@me').then((response)=>{
      setLoggedIn(true);
      setUserInfo(response.data)
    }).catch(()=>{
      setLoggedIn(false);
      setUserInfo(null);

    })
  }, [loggedIn]);



  return (
    <div style={{flex: 0}}>


    </div>
  );
}

export default App;
