import React from 'react'
import httpClients from '../httpClients'

export default function EventItem(props) {
    var event = props.event

    const deleteEvent = async () => {
        httpClients.delete('//localhost:5000/event/'+event.id).then(()=>{
            props.setDeletedElement(!props.deletedElement)
        }).catch(() =>{
            console.log('Error deleted')
        })
    }


    return (
    <div class="item">
        <div class="right floated content">
            <div onClick={() => deleteEvent()} class="ui button">Delete</div>
        </div>
        <i class="large github middle aligned icon"></i>
        <div class="content">
        <a class="header" onClick={() => props.setSelectedEvent(event)}>{event.nombre}</a>
        <div class="description">Fecha Inicio: {event.fecha_inicio}</div>
        </div>
    </div>
    )
}
